    console.log(cateBtn);
    cateBtn.e.target.className = `${classes.on}`;
  };

  return (
    <div className={classes.categori}>
      <ul>
        {cateType.map((cate) => (
          <li
            key={cate.id}
            type={cate.type}
            ref={cateBtn}
            // className={ }
            onClick={(e) => {
              filterHandler(e);
              alertHandler(e);
            }}
          >
            {cate.text}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Categori;
