import React from "react";

import classes from "./Categori.module.css";

const Categori = (props) => {
  const cateType = [
    { text: "ALL", id: "1", typesa: " " },
    { text: "UI/UX", id: "2", typesa: "UIUX" },
    { text: "WEB", id: "3", typesa: "WEB" },
    { text: "ETC", id: "4", typesa: "ETC" },
  ];

  const filterHandler = (e) => {
    e.preventDefault();
    props.filter(e.target.cateType);
    console.log(e.target.cateType);
  };

  const alertHandler = () => {
    alert();
  };

  return (
    <div className={classes.categori}>
      <ul>
        {cateType.map((cate) => (
          <button key={cate.id} cateType={cate.typesa} onClick={filterHandler}>
            {cate.text}
          </button>
        ))}
      </ul>
    </div>
  );
};

export default Categori;
